import React from "react";
import PropTypes from "prop-types";
import Modal from "react-bulma-components/lib/components/modal";
import Button from "react-bulma-components/lib/components/button";
import Section from "react-bulma-components/lib/components/section";
import axios from "axios";
import {
  getBrushes,
  getBottles,
  getRods,
  getWipers,
  getCaps,
} from "../middlewares/services";
import { useQuery } from "react-query";
import "../App.css";

export class OpenModal extends React.Component {
  static propTypes = {
    modal: PropTypes.object,
    children: PropTypes.node.isRequired,
    name: PropTypes.string,
  };

  static defaultProps = {
    modal: {},
    name: "name",
  };

  state = {
    show: false,
  };

  open = () => this.setState({ show: true });
  close = () => this.setState({ show: false });

  render() {
    return (
      <div>
        <Button color="dark" onClick={this.open}>
          {" "}
          {this.props.name}{" "}
        </Button>
        <br />
        <Modal
          show={this.state.show}
          onClose={this.close}
          {...this.props.modal}
        >
          {this.props.children}
        </Modal>
      </div>
    );
  }
}

export const BottleModal = () => {
  const addBottle = async (data) => {
    await axios.post("/bottle", {
      name: data.name,
      drawing: data.drawing,
      mold: data.mold,
      depth: data.depth,
      thread: data.thread
    })
    .then(function (response) {
      if (response.status === 200) {
        alert("Bottle added to the list!");
        console.log("Succesfully added");
      } else {
        const err = new Error(response.error);
        console.log(err);
        throw err;
      }
    })
    .catch(function (error) {
      alert(error);
    });
  };

  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div>
      <OpenModal modal={{ closeOnBlur: true }} name="Bottle">
        <div className="modal-body">
        <h1 className="modalText">Bottle</h1>
          {data.map((element) => (
            <Modal.Content>
              <Section style={{ backgroundColor: "white" }}>
                <p>
                  <strong>{element.name}</strong>
                  <small>{element.drawing}</small> <small>{element.mold}</small>
                  <br />
                </p>
                <Button
                  type="submit"
                  color="info"
                  onClick={() => {
                    addBottle(element);
                  }}
                >
                  Add Bottle
                </Button>
              </Section>
            </Modal.Content>
          ))}
        </div>
      </OpenModal>
    </div>
  );
};

export const BrushModal = () => {
  const addBrush = async (data) => {
    await axios.post("/brush", {
        brush: data.brush,
        original: data.original,
        shaftLength: data.shaftLength,
        shaftDiameter: data.shaftDiameter,
        brushLength: data.brushLength,
        brushDiameter: data.brushDiameter,
        type: data.type
      })
      .then(function (response) {
        if (response.status === 200) {
          alert("Brush added to the list!");
          console.log("Succesfully added");
        } else {
          const err = new Error(response.error);
          console.log(err);
          throw err;
        }
      })
      .catch(function (error) {
        alert(error);
      });
  };

  const { isLoading, error, data } = useQuery("brushes", getBrushes);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div>
      <OpenModal modal={{ closeOnBlur: true }} name="Brush">
        <div className="modal-body">
          <h1 className="modalText">Brush</h1>
          {data.map((element) => (
            <Modal.Content>
            <Section style={{ backgroundColor: "white" }}>
              <p>
                <strong>{element.brush}</strong>
                <small>{element.original}</small> <small>{element.type}</small>
                <br />
              </p>
              <Button
                type="submit"
                color="info"
                onClick={() => {
                  addBrush(element);
                }}
              >
                Add Brush
              </Button>
            </Section>
            </Modal.Content>
          ))}
        </div>
      </OpenModal>
    </div>
  );
};

