import React from 'react';
import '../App.css';
import Modal from 'react-bulma-components/lib/components/modal';
import Image from 'react-bulma-components/lib/components/image';
import Media from 'react-bulma-components/lib/components/media';
import Content from 'react-bulma-components/lib/components/content';
import Level from 'react-bulma-components/lib/components/level';
import Button from 'react-bulma-components/lib/components/button';
import { getBrushes, getBottles, getRods } from "../middlewares/services";
import { useQuery } from "react-query";

const BuildModal = ({name, id}) => {
  const { isLoading, error, data } = useQuery(name, id);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }
  return (
    <div>
    <Modal.Card>
      <Modal.Card.Head>
        <Modal.Card.Title>
        { name }
        </Modal.Card.Title>
      </Modal.Card.Head>

      { data.map( element =>

      <Modal.Card.Body>
        <Media>
          <Media.Item renderAs="figure" position="left">
            <Image size={64} alt="64x64" src="http://bulma.io/images/placeholders/128x128.png" />
          </Media.Item>
          <Media.Item>
            <Content>
              <p>
                <strong>{element.name }</strong> <small>{element.drawing}</small> <small>{element.drawing}</small>
                <br />
                Something else
              </p>
            </Content>
            <Level breakpoint="mobile">
              <Level.Side align="left">
                <Button link>Include</Button>
              </Level.Side>
            </Level>
          </Media.Item>
        </Media>
      </Modal.Card.Body>

      )}

      <Modal.Card.Foot style={{ alignItems: 'center', justifyContent: 'center' }}>
        <p>
          Bottles list
        </p>
      </Modal.Card.Foot>
    </Modal.Card>
    </div>
  );
}

export default BuildModal;
