//---------------- Filter component

const Filter = () => {

//---------------- hooks

  const [thread, setThread] = useState(false);
  const [rodBrush, setRodBrush] = useState(false);
  const [brushWiper, setBrushWiper] = useState(false);
  const [gap, setGap] = useState(false);
  const [bottle, setBottle] = useState([]);
  const [bottles, setBottles] = useState([]);
  const [rod, setRod] = useState([]);
  const [rods, setRods] = useState([]);
  const [wiper, setWiper] = useState([]);
  const [wipers, setWipers] = useState([]);
  const [brush, setBrush] = useState([]);
  const [brushes, setBrushes] = useState([]);
  const [claim, setClaim] = useState("");
  const [build, setBuild] = useState([]);
  const [buildClick, setBuildClick] = useState(false);

//---------------- hooks end

//---------------- useEffect

  useEffect(() => {
    const fetchBuild = async () => {
      const {data} = await axios.get("/build")
      .catch(function (error) {
        console.log(error)
      });
      setBuild(data);
      if (data[0] && data[0].bottle) {
        setBottle(data[0].bottle);
      } else {
        setBottle([])
      }
      if (data[0] && data[0].rod) {
        setRod(data[0].rod);
      } else {
        setRod([])
      }
      if (data[0] && data[0].brush) {
        setBrush(data[0].brush);
      } else {
        setBrush([])
      }
      if (data[0] && data[0].wiper) {
        setWiper(data[0].wiper);
      } else {
        setWiper([])
      }
    }

    const fetchBottles = async () => {
      const {data} = await axios.get("/bottle")
      .catch(function (error) {
        console.log(error)
      });
      setBottles(data);
    }

    const fetchBrushes = async () => {
      const {data} = await axios.get("/brush")
      .catch(function (error) {
        console.log(error)
      });
      setBrushes(data);
    }

    const fetchRods = async () => {
      const {data} = await axios.get("/rod")
      .catch(function (error) {
        console.log(error)
      });
      setRods(data);
    }

    const fetchWipers = async () => {
      const {data} = await axios.get("/wiper")
      .catch(function (error) {
        console.log(error)
      });
      setWipers(data);
    }

    fetchBuild();
    fetchBottles();
    fetchBrushes();
    fetchRods();
    fetchWipers();

    if (!build[0]) {
      setThread(false);
      setGap(false);
      setBrushWiper(false);
      setRodBrush(false);
      setWiper([]);
      setRod([]);
      setBrush([]);
      setBottle([]);
    }

    //------thread check
    if (build[0] && bottle && rod) {
      if (bottle.thread === rod.thread) {
        setThread(true);
      }
    } else {
      setThread(false);
    }
    //------thread check

    //------rod and brush check
    if (build[0] && brush.type && rod.name) {
      let brushRodDiff =  Number(rod.dimensions.brushDiameter) - Number(brush.shaftDiameter);
      if (brush.type === "INYECTADO") {
        if (brushRodDiff > 0.05 && brushRodDiff < 0.15){
          setRodBrush(true);
        } else {
          setRodBrush(false);
        }
      }
      if (brush.type === "NYLON") {
        if (brushRodDiff > -0.05 && brushRodDiff < 0.1){
          setRodBrush(true);
        } else {
          setRodBrush(false);
        }
      }
      if (brush.type === "LIP GLOSS") {
        if (brushRodDiff > 0 && brushRodDiff < 0.2){
          setRodBrush(true);
        } else {
          setRodBrush(false);
        }
      }
      if (brush.type === "DELINEADOR") {
        if (brushRodDiff > -0.05 && brushRodDiff < 0.1){
          setRodBrush(true);
        } else {
          setRodBrush(false);
        }
      }
    }
    //------rod and brush check

    //------wiper and brush check
    if (build[0] && brush.type && rod.name) {
      let brushWiperDiff =  Number(brush.brushDiameter) - Number(rod.dimensions.rodDiameter) ;
      if (brush.type === "INYECTADO") {
        if (brushWiperDiff > 0.5 && brushWiperDiff < 4.8){
          setBrushWiper(true);
        } else {
          setBrushWiper(false);
        }
      }
      if (brush.type === "NYLON") {
        if (brushWiperDiff > 0.8 && brushWiperDiff < 6.4){
          setBrushWiper(true);
        } else {
          setBrushWiper(false);
        }
      }
      if (brush.type === "LIP GLOSS") {
        if (brushWiperDiff > -3 && brushWiperDiff < 2){
          setBrushWiper(true);
        } else {
          setBrushWiper(false);
        }
      }
      if (brush.type === "DELINEADOR") {
        if (brushWiperDiff > 1 && brushWiperDiff < 2){
          setBrushWiper(true);
        } else {
          setBrushWiper(false);
        }
      }
    }
    //------wiper and brush check

    //------gap check
    if (build[0] && brush.type && rod.name && bottle.name) {
      let mascaraGap =  Number(bottle.depth) - (Number(brush.brushLength) + Number(rod.dimensions.length));
      if (brush.type === "INYECTADO") {
        if (mascaraGap > 2 && mascaraGap < 6){
          setGap(true);
        } else {
          setGap(false);
        }
      }
      if (brush.type === "NYLON") {
        if (mascaraGap > 1 && mascaraGap < 5){
          setGap(true);
        } else {
          setGap(false);
        }
      }
      if (brush.type === "LIP GLOSS") {
        if (mascaraGap > 1 && mascaraGap < 6){
          setGap(true);
        } else {
          setGap(false);
        }
      }
      if (brush.type === "DELINEADOR") {
        if (mascaraGap > 1 && mascaraGap < 5){
          setGap(true);
        } else {
          setGap(false);
        }
      }
    }
    //------gap check

}, [buildClick]);

//---------------- useEffect

//---------------- middlewares that must be moved and imported

  const deleteBuild = async () => {
    await axios.post("/delete")
    .then(function (response) {
      if (response.status === 200) {
        console.log("Succesfully deleted");
        setThread(false);
        setGap(false);
        setBrushWiper(false);
        setRodBrush(false);
        alert("Build deleted!");
      } else {
        const err = new Error(response.error);
        console.log(err);
        throw err;
      }
    })
    .catch(function (error) {
      alert(error);
    });
  };

  const removeBottleClick = () => {
    deleteBuild([]);
    setBuildClick(!buildClick);
  };

//---------------- middlewares that must be moved and imported end

//---------------- render
  return (
    <div>
      <Tile kind="ancestor" className="App">
        <Tile kind="parent" className="space">
          <Tile renderAs="article" kind="child" notification className="is-white border verticalPad">
          <Button.Group size="small">
            <Button
              type="submit"
              className="is-danger is-light"
              size="small"
              onClick={() => {
                setClaim("");
              }}
            >
              No claim
            </Button>
            <Button
              type="submit"
              className="is-link"
              size="small"
              onClick={() => {
                setClaim("definition");
              }}
            >
              Definition
            </Button>
            <Button
              type="submit"
              className="is-link"
              size="small"
              onClick={() => {
                setClaim("volumizing");
              }}
            >
              Volumizing
            </Button>
            <Button
              type="submit"
              className="is-link"
              size="small"
              onClick={() => {
                setClaim("lengthening");
              }}
            >
              Lengthening
            </Button>
            <Button
              type="submit"
              className="is-link"
              size="small"
              onClick={() => {
                setClaim("plumping");
              }}
            >
              Plumping
            </Button>
            <Button
              type="submit"
              className="is-link"
              size="small"
              onClick={() => {
                setClaim("curling");
              }}
            >
              Curling
            </Button>
          </Button.Group>
            {claim ?
              <div>
                <Tag color="dark"> { claim } </Tag>
              </div>
              : "" }
          </Tile>
          <Tile renderAs="article" kind="child" notification className="is-white border">
            <Button
              type="submit"
              className="is-danger is-light"
              size="small"
              onClick={() => {
                removeBottleClick();
              }}
            >
              Delete build!
            </Button>
            <br/>
            <br/>
            <Tag.Group className="center">
              <Tag color="dark"> Bottle </Tag>
              <Tag color="light"> {bottle.name}</Tag>
              <Tag color="dark"> Brush </Tag>
              <Tag color="light"> {brush.brush}</Tag>
              <Tag color="dark"> Rod </Tag>
              <Tag color="light"> {rod.name}</Tag>
              <Tag color="dark"> Wiper </Tag>
              <Tag color="light"> {wiper.name}</Tag>
            </Tag.Group>
          </Tile>
        </Tile>
      </Tile>

      <Tile kind="ancestor">
        <Tile kind="parent" className="App">
          <Tile renderAs="article" kind="child" notification color="white">
            <BottleFilter rod={rod} brush={brush} bottle={bottle} bottles={bottles} setBuildClick={setBuildClick} buildClick={buildClick}></BottleFilter>
          </Tile>
          <Tile renderAs="article" kind="child" notification color="white" className="center">
            <BrushFilter rod={rod} bottle={bottle} setBuildClick={setBuildClick} buildClick={buildClick} brush={brush} brushes={brushes} claim={claim}></BrushFilter>
          </Tile>
          <Tile renderAs="article" kind="child" notification color="white">
            <RodFilter rod={rod} rods={rods} bottle={bottle} setBuildClick={setBuildClick} buildClick={buildClick} brush={brush}></RodFilter>
          </Tile>
          <Tile renderAs="article" kind="child" notification color="white">
            <WiperFilter rod={rod} rods={rods} bottle={bottle} setBuildClick={setBuildClick} buildClick={buildClick} brush={brush} wiper={wiper} wipers={wipers}></WiperFilter>
          </Tile>
          {/* <Tile renderAs="article" kind="child" notification>
            <WiperFilter></WiperFilter>
          </Tile> */}
        </Tile>
      </Tile>

      {/* verifying assembly tiles*/}

      {/* Delete and reset*/}
    </div>
  );
}

