import React from 'react';
import '../App.css';
import { getBottles, getBrushes, getRods, getWipers, getCaps } from "../middlewares/services";
import { useQuery } from "react-query";
import Tile from 'react-bulma-components/lib/components/tile';
import Heading from 'react-bulma-components/lib/components/heading';
import Image from 'react-bulma-components/lib/components/image';
import Section from 'react-bulma-components/lib/components/section';
import Box from 'react-bulma-components/lib/components/box';
import { select } from '@storybook/addon-knobs';
import { colors } from "../features/colors.js"

export const Bottle = () => {
  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      { data.map( element =>
        <div>
        <Tile kind="ancestor">
          <Tile kind="parent">
            <Tile renderAs="article" kind="child" notification color={select("info", colors)}>
              <Heading>{element.name}</Heading>
              <Heading subtitle>{element.drawing}</Heading>

            </Tile>
          </Tile>
        </Tile>
    </div>

      )}
    </div>
  );
}

export const Brush = () => {
  const { isLoading, error, data } = useQuery("brushes", getBrushes);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.brush }>{ element.brush }</li>
      )}
      </header>
    </div>
  );
}

export const Rod = () => {
  const { isLoading, error, data } = useQuery("rods", getRods);
  if (isLoading) return "Loading... ";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Wiper = () => {
  const { isLoading, error, data } = useQuery("wipers", getWipers);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Cap = () => {
  const { isLoading, error, data } = useQuery("caps", getCaps);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}
