          </Tile>
          {/* <Tile renderAs="article" kind="child" notification>
            <WiperFilter></WiperFilter>
          </Tile> */}
        </Tile>
      </Tile>

      {/* verifying assembly tiles*/}

      {/* Delete and reset*/}
    </div>
  );
}

