import React, { useState } from "react";
import PropTypes from "prop-types";
import Modal from "react-bulma-components/lib/components/modal";
import Button from "react-bulma-components/lib/components/button";
import Content from "react-bulma-components/lib/components/content";
import Section from "react-bulma-components/lib/components/section";
import { select, boolean } from "@storybook/addon-knobs";
import {
  getBrushes,
  getBottles,
  getRods,
  getWipers,
  getCaps,
} from "../middlewares/services";
import { useQuery, useMutation } from "react-query";
import { colors } from "../features/colors.js";
import "../App.css";

export class OpenModal extends React.Component {
  static propTypes = {
    modal: PropTypes.object,
    children: PropTypes.node.isRequired,
    name: PropTypes.string,
  };

  static defaultProps = {
    modal: {},
    name: "name",
  };

  state = {
    show: false,
  };

  open = () => this.setState({ show: true });
  close = () => this.setState({ show: false });

  render() {
    return (
      <div>
        <Button color="info" onClick={this.open}>
          {" "}
          {this.props.name}{" "}
        </Button>
        <br />
        <Modal
          show={this.state.show}
          onClose={this.close}
          {...this.props.modal}
        >
          {this.props.children}
        </Modal>
      </div>
    );
  }
}

export const BottleModal = () => {
  const addBottle = ({ bottleName }) => {
    fetch("/bottle", {
      method: "POST",
      body: {
        bottleName: "hola",
      },
    })
      .then((res) => {
        if (res.status === 200) {
          alert("Bottle added to the list!");
          console.log("Succesfully added");
        } else {
          const err = new Error(res.error);
          console.log(err);
          throw err;
        }
      })
      .catch((err) => {
        console.error(err);
        alert("Error saving bottle. Please, try again!");
      });
  };
  const [bottleName, setBottleName] = useState("");
  const [mutate] = useMutation(addBottle);
  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  const onAddBottle = async (e) => {
    e.preventDefault();
    try {
      await mutate({ bottleName });
    } catch (error) {}
  };

  return (
    <div>
      <OpenModal modal={{ closeOnBlur: true }} name="Bottle">
        <div className="modal-body">
          {data.map((element) => (
            <Modal.Content>
              <Section style={{ backgroundColor: "white" }}>
                <p>
                  <strong>{element.name}</strong>{" "}
                  <small>{element.drawing}</small> <small>{element.mold}</small>
                  <br />
                  Prueba de modal
                </p>
                <Button
                  type="submit"
                  color="info"
                  onClick={() => {
                    addBottle(element.name);
                  }}
                >
                  Add
                </Button>
              </Section>
            </Modal.Content>
          ))}
        </div>
      </OpenModal>
    </div>
  );
};

