import React from 'react';
import '../App.css';
import Modal from 'react-bulma-components/lib/components/modal';
import Content from 'react-bulma-components/lib/components/content';
import Section from 'react-bulma-components/lib/components/section';
import Button from 'react-bulma-components/lib/components/button';
import OpenModal from './OpenModal.component';
import { select, boolean } from '@storybook/addon-knobs';
import { action } from '@storybook/addon-actions';
import { getBrushes, getBottles, getRods, getWipers, getCaps } from "../middlewares/services";
import { useQuery } from "react-query";
import { colors } from "../features/colors.js"

export const BottleModal = () => {
  const [showResults, setShowResults] = React.useState(true);
  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (

    <div className="modal-body">
    { showResults ?
      <div>
      { data.map( element =>
        <Modal.Content>
          <Section style={{ backgroundColor: 'white' }}>
          <p>
            <strong>{element.name }</strong> <small>{element.drawing}</small> <small>{element.mold}</small>
            <br />
            Something else
          </p>
            <Button color="info" onClick= {() => setShowResults(false)} >
              Add
            </Button>
          </Section>
        </Modal.Content>
      )}

      </div>

      : null }

    </div>

  );
}

const addBottle = () => {
  alert("Added bottle")
}

export const BrushModal = () => {
  const { isLoading, error, data } = useQuery("brushes", getBrushes);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }
  return (
    <div className="modal-body">

      { data.map( element =>
        <Modal.Content>
          <Section style={{ backgroundColor: 'white' }}>
          <p>
            <strong>{element.name }</strong> <small>{element.drawing}</small> <small>{element.mold}</small>
            <br />
            Something else
          </p>
          </Section>
        </Modal.Content>
      )}

    </div>
  );
}

export const RodModal = () => {
  const { isLoading, error, data } = useQuery("rods", getRods);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }
  return (
    <div className="modal-body">

      { data.map( element =>
        <Modal.Content>
          <Section style={{ backgroundColor: 'white' }}>
          <p>
            <strong>{element.name }</strong> <small>{element.drawing}</small> <small>{element.mold}</small>
            <br />
            Something else
          </p>
          </Section>
        </Modal.Content>
      )}

    </div>
  );
}

export const WiperModal = () => {
  const { isLoading, error, data } = useQuery("wipers", getWipers);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }
  return (
    <div className="modal-body">

      { data.map( element =>
        <Modal.Content>
          <Section style={{ backgroundColor: 'white' }}>
          <p>
            <strong>{element.name }</strong> <small>{element.drawing}</small> <small>{element.mold}</small>
            <br />
            Something else
          </p>
          </Section>
        </Modal.Content>
      )}

    </div>
  );
}

