import React from 'react';
import '../App.css';
import { getBottles, getBrushes, getRods, getWipers, getCaps } from "../middlewares/services";
import { useQuery } from "react-query";
import Tile from 'react-bulma-components/lib/components/tile';
import Heading from 'react-bulma-components/lib/components/heading';
import Image from 'react-bulma-components/lib/components/image';
import Section from 'react-bulma-components/lib/components/section';
import Box from 'react-bulma-components/lib/components/box';

export const Bottle = () => {
  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <div className="App">
        <Box
      paddingless={boolean('paddingless', false)}
      responsive={{
        mobile: {
          display: {
            value: 'block'
          },
        },
        tablet: {
          display: {
            value: 'flex'
          },
        },
        desktop: {
          display: {
            value: 'inline-flex',
            only: true,
          },
        },
        widescreen: {
          display: {
            value: 'inline-block',
          },
        },
      }}
      hide={{
        tablet: {
          hide: true,
          only: true,
        },
        widescreen: {
          hide: true,
        },
      }}
    >
      <Media>
        <Media.Item renderAs="figure" position="left">
          <Image size={64} alt="64x64" src="http://bulma.io/images/placeholders/128x128.png" />
        </Media.Item>
        <Media.Item>
          <Content>
            <p>
              <strong>John Smith</strong> <small>@johnsmith</small> <small>31m</small>
              <br />
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin ornare magna eros, eu pellentesque tortor vestibulum ut. Maecenas non massa sem. Etiam finibus odio quis feugiat facilisis.
            </p>
          </Content>
        </Media.Item>
      </Media>
    </Box>
    </div>

      )}
      </header>
    </div>
  );
}

export const Brush = () => {
  const { isLoading, error, data } = useQuery("brushes", getBrushes);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.brush }>{ element.brush }</li>
      )}
      </header>
    </div>
  );
}

export const Rod = () => {
  const { isLoading, error, data } = useQuery("rods", getRods);
  if (isLoading) return "Loading... ";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Wiper = () => {
  const { isLoading, error, data } = useQuery("wipers", getWipers);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Cap = () => {
  const { isLoading, error, data } = useQuery("caps", getCaps);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}
