import React from 'react';
import '../App.css';
import Media from 'react-bulma-components/lib/components/media';
import Image from 'react-bulma-components/lib/components/image';
import Content from 'react-bulma-components/lib/components/content';
import Button from 'react-bulma-components/lib/components/button';
import Level from 'react-bulma-components/lib/components/level';
import Section from 'react-bulma-components/lib/components/section';
import Box from 'react-bulma-components/lib/components/box';
import { Textarea, Field, Control } from 'react-bulma-components/lib/components/form';
import { getBottles, getBrushes, getRods, getWipers, getCaps } from "../middlewares/services";
import { useQuery } from "react-query";

export const Bottle = () => {
  const { isLoading, error, data } = useQuery("bottles", getBottles);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
        { data.map( element =>
          <Section key={element.name}>
            <Box>
              <Media>
                <Media.Item renderAs="figure" position="left">
                  <Image size={64} alt="64x64" src="http://bulma.io/images/placeholders/128x128.png" />
                </Media.Item>
                <Media.Item>
                  <Content>
                    <p renderAs="h6">
                      <strong>{element.name}</strong> <small>b</small> <small>n</small>
                      <br />
                      n
                    </p>
                  </Content>
                  <Level breakpoint="mobile">
                    <Level.Side align="left">
                      <Button link>Like</Button>
                      <Button link>Share</Button>
                    </Level.Side>
                  </Level>
                </Media.Item>
              </Media>
            </Box>
          </Section>
        )}
      </header>
    </div>
  );
}

export const Brush = () => {
  const { isLoading, error, data } = useQuery("brushes", getBrushes);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.brush }>{ element.brush }</li>
      )}
      </header>
    </div>
  );
}

export const Rod = () => {
  const { isLoading, error, data } = useQuery("rods", getRods);
  if (isLoading) return "Loading... ";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Wiper = () => {
  const { isLoading, error, data } = useQuery("wipers", getWipers);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}

export const Cap = () => {
  const { isLoading, error, data } = useQuery("caps", getCaps);
  console.log(data);
  if (isLoading) return "Loading...";
  if (error) {
    return "Oops! " + error.message;
  }

  return (
    <div className="App">
      <header className="App-header">
      { data.map( element =>
        <li key = { element.name }>{ element.name }</li>
      )}
      </header>
    </div>
  );
}
