import axios from "axios";
import { useQuery } from "react-query";

export const getBottles = () => {
  return fetch("/bottle")
  .then(response => response.json());
}
export const getBrushes = () => {
  return fetch("/brush")
  .then(response => response.json());
}
export const getCaps = () => {
  return fetch("/cap")
  .then(response => response.json());
}
export const getRods = () => {
  return fetch("/rod")
  .then(response => response.json());
}
export const getWipers = () => {
  return fetch("/wiper")
  .then(response => response.json());
}
export const getBuild = async () => {
 const { data } = await axios.get("/build");
 return data;
};

export const mascaraBuild = () => {
  const { isLoading, error, data } = useQuery("build", getBuild);
  if (isLoading) return "Loading...";
  if (error) return "Oops! ";
  return data;
};
